    winchDown.reset(new JoystickButton(shooterJoystick.get(), 9));
    winchDown->WhileHeld(new RunWinch(-0.3));
    winchUp.reset(new JoystickButton(driverJoystick.get(), 7));
    winchUp->WhileHeld(new RunWinch(0.3));

